DROP TABLE IF EXISTS leprocs;
CREATE TABLE leprocs ( profession varchar(20) NOT NULL, name varchar(50) NOT NULL, research_name varchar(50) DEFAULT NULL, research_lvl INT NOT NULL, proc_type char(6) DEFAULT NULL, chance varchar(20) DEFAULT NULL, modifiers varchar(255) NOT NULL, duration varchar(20) NOT NULL, proc_trigger varchar(20) NOT NULL, description varchar(255) NOT NULL );
