CREATE TABLE IF NOT EXISTS poll (id INT NOT NULL PRIMARY KEY AUTO_INCREMENT, question VARCHAR(1024) NOT NULL, status VARCHAR(20) NOT NULL, duration INT NOT NULL, min_access_level VARCHAR(20) NOT NULL, char_id INT NOT NULL, created_at INT NOT NULL, finished_at INT NOT NULL, is_finished TINYINT NOT NULL);
CREATE TABLE IF NOT EXISTS poll_choice (id INT NOT NULL PRIMARY KEY AUTO_INCREMENT, poll_id INT NOT NULL, choice VARCHAR(1024));