DROP TABLE IF EXISTS tower_site;
CREATE TABLE tower_site (playfield_id INT NOT NULL, site_number SMALLINT NOT NULL, min_ql SMALLINT NOT NULL, max_ql SMALLINT NOT NULL, x_coord SMALLINT NOT NULL, y_coord SMALLINT NOT NULL, close_time INT, site_name varchar(50) NOT NULL, enabled TINYINT NOT NULL, PRIMARY KEY (playfield_id, site_number));
